package java_exam1;

public class exam2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int num1 = 10;
	    int num2 = 20;
	    
	    if(num1>num2) {
	    	System.out.println("left");
	    	System.out.println(num1-num2);
	    }
	    if(num1<num2) {
	    	System.out.println("right");
	    	System.out.println(num2-num1);
	    }
	    if(num1 == num2) {
	    	System.out.println("equal");
	    }
	}

}
