package java_exam1;

public class exam3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 100;
		System.out.println(num1);
		num1 = num1+10;
		System.out.println(num1);
		num1 = num1/10;
		System.out.println(num1);
		num1 = num1-10;
		System.out.println(num1);
		num1 = num1*10;
		System.out.println(num1);
		
		//1. 변수 선언(만들어야) 사용 가능.
		//2. 변수 선언 = 자료형(타입) 변수명;
		//3. 변수는 = 왼쪽 : 저장소, 오른쪽 : 값
		//4. 변수는 중복 선언 X
		//5. 변수는 단일값만 저장 가능 => 새로운 값을 저장하면 기존의 값 상실
		//6. 변수는 재활용 가능.
	}

}
